import json


class FinancialIndicators:
    def __init__(self, json_data):
        self.data = json.loads(json_data)
        self.financials = self.data["results"][0]["financials"]
        self.process_data()

    def process_data(self):
        self.gross_margin = self.calculate_gross_margin()
        self.net_margin = self.calculate_net_margin()
        self.long_term_debt_over_earnings = (
            self.calculate_long_term_debt_over_earnings()
        )
        self.capital_expenditure_over_net_income = (
            self.calculate_capital_expenditure_over_net_income()
        )
        self.pe_ratio = self.calculate_pe_ratio()

    def calculate_gross_margin(self):
        try:
            gross_profit = self.financials["income_statement"]["gross_profit"]["value"]
            revenues = self.financials["income_statement"]["revenues"]["value"]
            return gross_profit / revenues
        except KeyError:
            return None

    def calculate_net_margin(self):
        try:
            net_income = self.financials["income_statement"]["net_income_loss"]["value"]
            revenues = self.financials["income_statement"]["revenues"]["value"]
            return net_income / revenues
        except KeyError:
            return None

    def calculate_long_term_debt_over_earnings(self):
        try:
            long_term_debt = self.financials["balance_sheet"]["long_term_debt"]["value"]
            earnings = self.financials["income_statement"]["net_income_loss"]["value"]
            return long_term_debt / earnings
        except KeyError:
            return None

    def calculate_capital_expenditure_over_net_income(self):
        try:
            capital_expenditure = self.financials["cash_flow_statement"][
                "net_cash_flow_from_investing_activities"
            ]["value"]
            net_income = self.financials["income_statement"]["net_income_loss"]["value"]
            return capital_expenditure / net_income
        except KeyError:
            return None

    def calculate_pe_ratio(self):
        try:
            market_cap = self.financials["balance_sheet"].get("market_cap", None)
            net_income = self.financials["income_statement"]["net_income_loss"]["value"]
            diluted_shares_outstanding = self.financials["income_statement"][
                "diluted_average_shares"
            ]["value"]
            earnings_per_share = net_income / diluted_shares_outstanding
            pe_ratio = market_cap / (earnings_per_share * diluted_shares_outstanding)
            return pe_ratio
        except KeyError:
            return None

    def get_indicators(self):
        return {
            "Gross Margin": self.gross_margin,
            "Net Margin": self.net_margin,
            "Long-Term Debt over Earnings": self.long_term_debt_over_earnings,
            "Capital Expenditure over Net Income": self.capital_expenditure_over_net_income,
            "P/E Ratio": self.pe_ratio,
        }


